	$message=$_POST['message'];
	if($name=="")
	{
		echo "<script>alert('Please Enter Name');</script>";
	}
	else if($phone=="")
	{
		echo "<script>alert('Please Enter phone');</script>";

	}
	else if($subject=="")
	{
		echo "<script>alert('Please Enter subject');</script>";

	}
	else if($message=="")
	{
		echo "<script>alert('Please Enter message');</script>";

	}
	else
	{
		$to = "nirgariberasha@gmail.com"; 
		//$headers = 'Content-type: text/html;' . "\r\n".'From: contact@worldspace.net' . "\r\n\n" .'Reply-To: ontact@worldspace.net' . "\r\n";
