    <script type="text/javascript">
		jQuery(document).ready(function($){

			$('.fw_demo_color_picker').wpColorPicker({
				defaultColor: '#96c052',
				change: function(event, ui){
					
					var scheme = $('style[title="fw_color_scheme"]').text();
					var color = ui.color.toString();
					var cookie_color = $.cookie('fw_color_scheme');
					if( cookie_color === undefined ){
						$.cookie('fw_color_scheme', '#96c052');
						cookie_color = $.cookie('fw_color_scheme');
					}

					$.cookie('fw_color_scheme', color );
					var re = new RegExp(cookie_color, 'g');
					var s_text = scheme.replace( re, color );
					$('style[title="fw_color_scheme"]').text( s_text );
					//console.log( scheme );
					
				},
				palettes: true,
				hide: false,
			});
			$('.clickto_clor_picker').click(function(e) {
                e.preventDefault();
				if( $(this).hasClass('fw-hidden') ){
					$(this).parent().animate({"margin-left": '+=308'}, {duration: 1200});
					$(this).removeClass('fw-hidden');
				}else{
					$(this).parent().animate({"margin-left": '-=308'}, {duration: 1200});
					$(this).addClass('fw-hidden');
				}
            });
		});
	</script>
    
